pragma solidity ^0.6.0;


import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "../interfaces/ILFO.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";


/**
* @title Artists self minting for LongboardFamara
*
* Allows for the edition artists to mint their own assets and control the price of an edition
*
*/

contract ArtistEditionControls is AccessControl, Pausable {
  using SafeMath for uint256;

  // Interface into the LFO world
  ILFO public iLFOAddress;

  event PriceChanged(
    uint256 indexed _editionNumber,
    address indexed _artist,
    uint256 _priceInWei
  );

  event EditionGifted(
    uint256 indexed _editionNumber,
    address indexed _artist,
    uint256 indexed _tokenId
  );

  event EditionDeactivated(
    uint256 indexed _editionNumber
  );

  bool public deactivationPaused = false;

    //ROLES
    bytes32 public constant WHITELISTED = keccak256("WHITELISTED");


  modifier whenDeactivationNotPaused() {
    require(!deactivationPaused);
    _;
  }

modifier onlyWhitelisted {
        require(hasRole(WHITELISTED, msg.sender));
        _;
    }


  constructor(ILFO _iLFOAddress) public {
    iLFOAddress = _iLFOAddress;
    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _setupRole(WHITELISTED, msg.sender);

  }

  /**
   * @dev Ability to gift new NFTs to an address, from a LFO edition
   * @dev Only callable from edition artists defined in LFO NFT contract
   * @dev Only callable when contract is not paused
   * @dev Reverts if edition is invalid
   * @dev Reverts if edition is not active in LFO NFT contract
   */
  function gift(address _receivingAddress, uint256 _editionNumber)
  external
  whenNotPaused
  returns (uint256)
  {
    require(_receivingAddress != address(0), "Unable to send to zero address");

    (address artistAccount, ) = iLFOAddress.artistCommission(_editionNumber);

    require(msg.sender == artistAccount || hasRole(WHITELISTED, msg.sender), "Only from the edition artist account");

    bool isActive = iLFOAddress.editionActive(_editionNumber);
    require(isActive, "Only when edition is active");

    uint256 tokenId = iLFOAddress.mint(_receivingAddress, _editionNumber);

    emit EditionGifted(_editionNumber, msg.sender, tokenId);

    return tokenId;
  }

  /**
   * @dev Sets the price of the provided edition in the WEI
   * @dev Only callable from edition artists defined in LFO NFT contract
   * @dev Only callable when contract is not paused
   * @dev Reverts if edition is invalid
   */
  function updateEditionPrice(uint256 _editionNumber, uint256 _priceInWei)
  external
  whenNotPaused
  returns (bool)
  {
    (address artistAccount, ) = iLFOAddress.artistCommission(_editionNumber);
    require(msg.sender == artistAccount || hasRole(WHITELISTED, msg.sender), "Only from the edition artist account");

    iLFOAddress.updatePriceInWei(_editionNumber, _priceInWei);

    emit PriceChanged(_editionNumber, msg.sender, _priceInWei);

    return true;
  }

  /**
   * @dev Sets provided edition to deactivated so it does not appear on the platform
   * @dev Only callable from edition artists defined in LFO NFT contract
   * @dev Only callable when contract is not paused
   * @dev Reverts if edition is invalid
   * @dev Reverts if edition is not active in LFO NFT contract
   */
  function deactivateEdition(uint256 _editionNumber)
  external
  whenNotPaused
  whenDeactivationNotPaused
  returns (bool)
  {
    (address payable artistAccount, ) = iLFOAddress.artistCommission(_editionNumber);
    require(msg.sender == artistAccount || hasRole(WHITELISTED, msg.sender), "Only from the edition artist account");

    // Only allow them to be disabled if we have not already done it already
    bool isActive = iLFOAddress.editionActive(_editionNumber);
    require(isActive, "Only when edition is active");

    iLFOAddress.updateActive(_editionNumber, false);

    emit EditionDeactivated(_editionNumber);

    return true;
  }

  /**
   * @dev Sets the LFO address
   * @dev Only callable from owner
   */
  function setLFO(ILFO _iLFOAddress) onlyWhitelisted public {
    iLFOAddress = _iLFOAddress;
  }

  /**
   * @dev Disables the ability to deactivate editions from this contract
   * @dev Only callable from owner
   */
  function pauseDeactivation() onlyWhitelisted public {
    deactivationPaused = true;
  }

  /**
   * @dev Enables the ability to deactivate editions from this contract
   * @dev Only callable from owner
   */
  function enablesDeactivation() onlyWhitelisted public {
    deactivationPaused = false;
  }

}

